import { useState, useEffect } from 'react';
import { Container, Row, Col, Card, Form, Button } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';

const Home = () => {
    const [blogs, setBlogs] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [searchTerm, setSearchTerm] = useState('');
    const { user } = useAuth();

    useEffect(() => {
        fetchBlogs();
    }, []);

    const fetchBlogs = async () => {
        try {
            const response = await fetch('http://localhost:3001/blogs');
            const data = await response.json();

            if (!response.ok) {
                throw new Error(data.message || 'Errore nel caricamento dei blog');
            }

            setBlogs(data);
        } catch (err) {
            setError(err.message);
        } finally {
            setLoading(false);
        }
    };

    const filteredBlogs = blogs.filter(blog => 
        blog.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
        blog.content.toLowerCase().includes(searchTerm.toLowerCase())
    );

    if (loading) return <div className="text-center mt-5">Caricamento...</div>;
    if (error) return <div className="text-center mt-5 text-danger">{error}</div>;

    return (
        <Container className="mt-4">
            <Row className="mb-4">
                <Col>
                    <Form.Control
                        type="text"
                        placeholder="Cerca nei blog..."
                        value={searchTerm}
                        onChange={(e) => setSearchTerm(e.target.value)}
                    />
                </Col>
                {user && (
                    <Col xs="auto">
                        <Link to="/create-blog">
                            <Button variant="primary">Crea Blog</Button>
                        </Link>
                    </Col>
                )}
            </Row>

            <Row>
                {filteredBlogs.map(blog => (
                    <Col key={blog._id} md={4} className="mb-4">
                        <Card>
                            {blog.cover && (
                                <Card.Img 
                                    variant="top" 
                                    src={blog.cover} 
                                    alt={blog.title}
                                    style={{ height: '200px', objectFit: 'cover' }}
                                />
                            )}
                            <Card.Body>
                                <Card.Title>{blog.title}</Card.Title>
                                <Card.Text>
                                    {blog.content.substring(0, 150)}...
                                </Card.Text>
                                <div className="d-flex justify-content-between align-items-center">
                                    <small className="text-muted">
                                        Autore: {blog.author.name} {blog.author.lastName}
                                    </small>
                                    <Link to={`/blog/${blog._id}`}>
                                        <Button variant="outline-primary">Leggi di più</Button>
                                    </Link>
                                </div>
                            </Card.Body>
                        </Card>
                    </Col>
                ))}
            </Row>
        </Container>
    );
};

export default Home;
