import { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { Container, Row, Col, Card, Button, Alert } from 'react-bootstrap';
import { useAuth } from '../contexts/AuthContext';

const BlogDetail = () => {
    const { id } = useParams();
    const navigate = useNavigate();
    const { user } = useAuth();
    const [blog, setBlog] = useState(null);
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchBlog = async () => {
            try {
                const response = await fetch(`http://localhost:3001/blogs/${id}`);
                const data = await response.json();

                if (!response.ok) {
                    throw new Error(data.message || 'Errore nel caricamento del blog');
                }

                setBlog(data);
            } catch (err) {
                setError(err.message);
            } finally {
                setLoading(false);
            }
        };

        fetchBlog();
    }, [id]);

    const handleDelete = async () => {
        if (!window.confirm('Sei sicuro di voler eliminare questo blog?')) {
            return;
        }

        try {
            const response = await fetch(`http://localhost:3001/blogs/${id}`, {
                method: 'DELETE'
            });

            if (!response.ok) {
                throw new Error('Errore durante l\'eliminazione del blog');
            }

            navigate('/');
        } catch (err) {
            setError(err.message);
        }
    };

    if (loading) {
        return (
            <Container className="mt-5">
                <div className="text-center">Caricamento...</div>
            </Container>
        );
    }

    if (error) {
        return (
            <Container className="mt-5">
                <Alert variant="danger">{error}</Alert>
            </Container>
        );
    }

    if (!blog) {
        return (
            <Container className="mt-5">
                <Alert variant="warning">Blog non trovato</Alert>
            </Container>
        );
    }

    const isAuthor = user && (user._id === blog.author._id || user.role === 'admin');

    return (
        <Container className="mt-5">
            <Row className="justify-content-md-center">
                <Col md={10}>
                    <Card>
                        {blog.cover && (
                            <Card.Img 
                                variant="top" 
                                src={blog.cover} 
                                alt={blog.title}
                                style={{ height: '400px', objectFit: 'cover' }}
                            />
                        )}
                        <Card.Body>
                            <Card.Title className="h2 mb-4">{blog.title}</Card.Title>
                            <div className="d-flex justify-content-between align-items-center mb-4">
                                <div>
                                    <p className="mb-1">
                                        <strong>Autore:</strong> {blog.author.firstName} {blog.author.lastName}
                                    </p>
                                    <p className="mb-1">
                                        <strong>Categoria:</strong> {blog.category}
                                    </p>
                                    <p className="mb-1">
                                        <strong>Tempo di lettura:</strong> {blog.readTime.value} {blog.readTime.unit}
                                    </p>
                                </div>
                                {isAuthor && (
                                    <div>
                                        <Button 
                                            variant="primary" 
                                            className="me-2"
                                            onClick={() => navigate(`/edit-blog/${id}`)}
                                        >
                                            Modifica
                                        </Button>
                                        <Button 
                                            variant="danger"
                                            onClick={handleDelete}
                                        >
                                            Elimina
                                        </Button>
                                    </div>
                                )}
                            </div>
                            <Card.Text className="blog-content">
                                {blog.content}
                            </Card.Text>
                        </Card.Body>
                    </Card>
                </Col>
            </Row>
        </Container>
    );
};

export default BlogDetail;
