import { useState, useEffect } from 'react';
import { Form, Button, Container, Alert } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const CreateBlog = () => {
  const [formData, setFormData] = useState({
    title: '',
    content: '',
    author: '',
    cover: null
  });
  const [authors, setAuthors] = useState([]);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    fetchAuthors();
  }, []);

  const fetchAuthors = async () => {
    try {
      const response = await axios.get('http://localhost:3001/authors');
      setAuthors(response.data);
    } catch (err) {
      console.error('Errore nel caricamento degli autori:', err);
      setError('Errore nel caricamento degli autori');
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const formDataToSend = new FormData();
      formDataToSend.append('title', formData.title);
      formDataToSend.append('content', formData.content);
      formDataToSend.append('author', formData.author);
      if (formData.cover) {
        formDataToSend.append('cover', formData.cover);
      }

      await axios.post('http://localhost:3001/api/blogs', formDataToSend, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });
      navigate('/blogs');
    } catch (err) {
      console.error('Errore durante la creazione:', err);
      setError(err.response?.data?.message || 'Errore durante la creazione del blog');
    }
  };

  const handleChange = (e) => {
    if (e.target.name === 'cover') {
      setFormData({ ...formData, cover: e.target.files[0] });
    } else {
      setFormData({ ...formData, [e.target.name]: e.target.value });
    }
  };

  return (
    <Container className="mt-4">
      <h2>Crea Nuovo Blog</h2>
      {error && <Alert variant="danger">{error}</Alert>}
      <Form onSubmit={handleSubmit}>
        <Form.Group className="mb-3">
          <Form.Label>Titolo</Form.Label>
          <Form.Control
            type="text"
            name="title"
            value={formData.title}
            onChange={handleChange}
            required
          />
        </Form.Group>
        <Form.Group className="mb-3">
          <Form.Label>Contenuto</Form.Label>
          <Form.Control
            as="textarea"
            rows={5}
            name="content"
            value={formData.content}
            onChange={handleChange}
            required
          />
        </Form.Group>
        <Form.Group className="mb-3">
          <Form.Label>Autore</Form.Label>
          <Form.Select
            name="author"
            value={formData.author}
            onChange={handleChange}
            required
          >
            <option value="">Seleziona un autore</option>
            {authors.map(author => (
              <option key={author._id} value={author._id}>
                {author.name}
              </option>
            ))}
          </Form.Select>
        </Form.Group>
        <Form.Group className="mb-3">
          <Form.Label>Immagine di copertina</Form.Label>
          <Form.Control
            type="file"
            name="cover"
            onChange={handleChange}
            accept="image/*"
            required
          />
        </Form.Group>
        <Button variant="primary" type="submit">
          Crea Blog
        </Button>
      </Form>
    </Container>
  );
};

export default CreateBlog;
