import { useState, useEffect } from 'react';
import { Form, Button, Container, Alert } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const CreateBlog = () => {
  const navigate = useNavigate();
  const [authors, setAuthors] = useState([]);
  const [error, setError] = useState('');
  const [formData, setFormData] = useState({
    title: '',
    content: '',
    category: '',
    readTime: {
      value: '',
      unit: 'minuti'
    },
    author: '',
    cover: null
  });

  useEffect(() => {
    const fetchAuthors = async () => {
      try {
        const response = await axios.get('http://localhost:3001/authors');
        setAuthors(response.data);
      } catch (err) {
        setError('Errore nel caricamento degli autori');
      }
    };
    fetchAuthors();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // Prima creiamo il blog senza l'immagine
      const blogData = {
        title: formData.title,
        content: formData.content,
        category: formData.category,
        author: formData.author,
        readTime: {
          value: parseInt(formData.readTime.value),
          unit: formData.readTime.unit
        }
      };

      // Creiamo il blog
      const response = await axios.post('http://localhost:3001/blogs', blogData);

      // Se abbiamo un'immagine, la carichiamo separatamente
      if (formData.cover && response.data._id) {
        const imageFormData = new FormData();
        imageFormData.append('cover', formData.cover);

        await axios.put(`http://localhost:3001/blogs/${response.data._id}`, imageFormData, {
          headers: {
            'Content-Type': 'multipart/form-data'
          }
        });
      }

      navigate('/blogs');
    } catch (err) {
      console.error('Errore durante la creazione:', err);
      setError(err.response?.data?.message || 'Errore durante la creazione del blog');
    }
  };

  const handleChange = (e) => {
    if (e.target.name === 'cover') {
      setFormData({ ...formData, cover: e.target.files[0] });
    } else if (e.target.name.startsWith('readTime.')) {
      const field = e.target.name.split('.')[1];
      setFormData({
        ...formData,
        readTime: { ...formData.readTime, [field]: e.target.value }
      });
    } else {
      setFormData({ ...formData, [e.target.name]: e.target.value });
    }
  };

  return (
    <Container className="mt-4">
      <h2>Crea Nuovo Blog</h2>
      {error && <Alert variant="danger">{error}</Alert>}
      
      <Form onSubmit={handleSubmit}>
        <Form.Group className="mb-3">
          <Form.Label>Titolo</Form.Label>
          <Form.Control
            type="text"
            name="title"
            value={formData.title}
            onChange={handleChange}
            required
          />
        </Form.Group>

        <Form.Group className="mb-3">
          <Form.Label>Categoria</Form.Label>
          <Form.Control
            type="text"
            name="category"
            value={formData.category}
            onChange={handleChange}
            required
          />
        </Form.Group>

        <Form.Group className="mb-3">
          <Form.Label>Contenuto</Form.Label>
          <Form.Control
            as="textarea"
            rows={5}
            name="content"
            value={formData.content}
            onChange={handleChange}
            required
          />
        </Form.Group>

        <Form.Group className="mb-3">
          <Form.Label>Tempo di Lettura</Form.Label>
          <div className="d-flex gap-2">
            <Form.Control
              type="number"
              name="readTime.value"
              value={formData.readTime.value}
              onChange={handleChange}
              required
              min="1"
            />
            <Form.Select
              name="readTime.unit"
              value={formData.readTime.unit}
              onChange={handleChange}
            >
              <option value="minuti">Minuti</option>
              <option value="ore">Ore</option>
            </Form.Select>
          </div>
        </Form.Group>

        <Form.Group className="mb-3">
          <Form.Label>Autore</Form.Label>
          <Form.Select
            name="author"
            value={formData.author}
            onChange={handleChange}
            required
          >
            <option value="">Seleziona un autore</option>
            {authors.map(author => (
              <option key={author._id} value={author._id}>
                {author.name} {author.lastName}
              </option>
            ))}
          </Form.Select>
        </Form.Group>

        <Form.Group className="mb-3">
          <Form.Label>Immagine di Copertina</Form.Label>
          <Form.Control
            type="file"
            name="cover"
            onChange={handleChange}
            accept="image/*"
            required
          />
        </Form.Group>

        <Button variant="primary" type="submit">
          Crea Blog
        </Button>
      </Form>
    </Container>
  );
};

export default CreateBlog;
