import { useState, useEffect } from 'react';
import { Container, Row, Col, Card, Button, Alert } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';

const MyBlogs = () => {
    const { user } = useAuth();
    const [blogs, setBlogs] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    useEffect(() => {
        fetchMyBlogs();
    }, []);

    const fetchMyBlogs = async () => {
        try {
            const response = await fetch(`http://localhost:3001/blogs/author/${user._id}`);
            const data = await response.json();

            if (!response.ok) {
                throw new Error(data.message || 'Errore nel caricamento dei tuoi blog');
            }

            setBlogs(data);
        } catch (err) {
            setError(err.message);
        } finally {
            setLoading(false);
        }
    };

    const handleDelete = async (id) => {
        if (!window.confirm('Sei sicuro di voler eliminare questo blog?')) {
            return;
        }

        try {
            const response = await fetch(`http://localhost:3001/blogs/${id}`, {
                method: 'DELETE'
            });

            if (!response.ok) {
                throw new Error('Errore durante l\'eliminazione del blog');
            }

            setBlogs(blogs.filter(blog => blog._id !== id));
        } catch (err) {
            setError(err.message);
        }
    };

    if (loading) {
        return (
            <Container className="mt-5">
                <div className="text-center">Caricamento...</div>
            </Container>
        );
    }

    if (error) {
        return (
            <Container className="mt-5">
                <Alert variant="danger">{error}</Alert>
            </Container>
        );
    }

    return (
        <Container className="mt-5">
            <Row className="mb-4">
                <Col>
                    <h2>I miei blog</h2>
                    <Button 
                        variant="primary" 
                        as={Link} 
                        to="/create-blog"
                        className="mb-4"
                    >
                        Crea nuovo blog
                    </Button>
                </Col>
            </Row>

            {blogs.length === 0 ? (
                <Alert variant="info">
                    Non hai ancora creato nessun blog.
                </Alert>
            ) : (
                <Row>
                    {blogs.map(blog => (
                        <Col key={blog._id} md={6} lg={4} className="mb-4">
                            <Card>
                                {blog.cover && (
                                    <Card.Img 
                                        variant="top" 
                                        src={blog.cover} 
                                        alt={blog.title}
                                        style={{ height: '200px', objectFit: 'cover' }}
                                    />
                                )}
                                <Card.Body>
                                    <Card.Title>{blog.title}</Card.Title>
                                    <Card.Text>
                                        {blog.content.substring(0, 150)}...
                                    </Card.Text>
                                    <div className="d-flex justify-content-between align-items-center">
                                        <div>
                                            <small className="text-muted">
                                                {blog.readTime.value} {blog.readTime.unit} di lettura
                                            </small>
                                        </div>
                                        <div>
                                            <Button 
                                                variant="outline-primary" 
                                                as={Link} 
                                                to={`/blog/${blog._id}`}
                                                className="me-2"
                                            >
                                                Leggi
                                            </Button>
                                            <Button 
                                                variant="outline-secondary" 
                                                as={Link} 
                                                to={`/edit-blog/${blog._id}`}
                                                className="me-2"
                                            >
                                                Modifica
                                            </Button>
                                            <Button 
                                                variant="outline-danger"
                                                onClick={() => handleDelete(blog._id)}
                                            >
                                                Elimina
                                            </Button>
                                        </div>
                                    </div>
                                </Card.Body>
                            </Card>
                        </Col>
                    ))}
                </Row>
            )}
        </Container>
    );
};

export default MyBlogs;
