import { useState, useEffect } from 'react';
import { Container, Card, Button, Alert, Spinner } from 'react-bootstrap';
import { useNavigate, useParams } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import axios from 'axios';

const PostDetail = () => {
  const [post, setPost] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [deleting, setDeleting] = useState(false);
  const navigate = useNavigate();
  const { id } = useParams();
  const { user } = useAuth();

  useEffect(() => {
    fetchPost();
  }, [id]);

  const fetchPost = async () => {
    try {
      const response = await axios.get(`/api/blogs/${id}`);
      setPost(response.data);
      setLoading(false);
    } catch (err) {
      setError('Errore nel caricamento del post');
      setLoading(false);
    }
  };

  const handleDelete = async () => {
    if (!window.confirm('Sei sicuro di voler eliminare questo post?')) {
      return;
    }

    setDeleting(true);
    try {
      await axios.delete(`/api/blogs/${id}`);
      navigate('/');
    } catch (err) {
      setError('Errore durante l\'eliminazione del post');
    } finally {
      setDeleting(false);
    }
  };

  if (loading) {
    return (
      <Container className="text-center mt-5">
        <Spinner animation="border" role="status">
          <span className="visually-hidden">Caricamento...</span>
        </Spinner>
      </Container>
    );
  }

  if (error) {
    return (
      <Container className="mt-5">
        <Alert variant="danger">{error}</Alert>
      </Container>
    );
  }

  if (!post) {
    return (
      <Container className="mt-5">
        <Alert variant="warning">Post non trovato</Alert>
      </Container>
    );
  }

  return (
    <Container className="mt-5">
      <Card>
        <Card.Header className="d-flex justify-content-between align-items-center">
          <div>
            <h4 className="mb-0">{post.title}</h4>
            <small className="text-muted">
              Di {post.author.name} {post.author.lastName} â€¢ 
              {new Date(post.createdAt).toLocaleDateString()}
            </small>
          </div>
          <span className="badge bg-primary">{post.category}</span>
        </Card.Header>
        <Card.Body>
          <Card.Text className="whitespace-pre-wrap">{post.content}</Card.Text>
          {user && (user._id === post.author._id || user.isAdmin) && (
            <div className="d-flex justify-content-end gap-2 mt-4">
              <Button 
                variant="outline-secondary" 
                onClick={() => navigate(`/blogs/${id}/edit`)}
                disabled={deleting}
              >
                Modifica
              </Button>
              <Button 
                variant="outline-danger" 
                onClick={handleDelete}
                disabled={deleting}
              >
                {deleting ? 'Eliminazione...' : 'Elimina'}
              </Button>
            </div>
          )}
        </Card.Body>
      </Card>
    </Container>
  );
};

export default PostDetail;
