import { useState, useEffect } from 'react';
import { Container, Row, Col, Alert } from 'react-bootstrap';
import { useAuth } from '../contexts/AuthContext';
import axios from 'axios';
import BlogCard from '../components/BlogCard';

const MyBlogs = () => {
  const { user } = useAuth(); // Ottieni i dati dell'utente loggato
  const [blogs, setBlogs] = useState([]);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchMyBlogs = async () => {
      try {
        setLoading(true);
        // Effettua una richiesta alla nuova rotta /my-blogs
        const response = await axios.get(`http://localhost:3001/blogs/my-blogs?author=${user._id}`);
        setBlogs(response.data.blogs); // Imposta i blog ricevuti nello stato
      } catch (err) {
        setError('Errore nel caricamento dei tuoi blog');
      } finally {
        setLoading(false);
      }
    };

    if (user) {
      fetchMyBlogs(); // Chiama la funzione solo se l'utente è loggato
    }
  }, [user]);

  return (
    <Container className="mt-5">
      <h1>I Miei Blog</h1>
      {error && <Alert variant="danger">{error}</Alert>}
      <Row>
        {loading ? (
          <p>Caricamento in corso...</p>
        ) : blogs.length > 0 ? (
          blogs.map((blog) => (
            <Col key={blog._id} md={4} className="mb-4">
              <BlogCard blog={blog} user={user} /> {/* Mostra i blog con il componente BlogCard */}
            </Col>
          ))
        ) : (
          <p>Non hai ancora pubblicato nessun blog</p>
        )}
      </Row>
    </Container>
  );
};

export default MyBlogs;
