import { Card, Button, Badge } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';

const BlogCard = ({ blog, user, onDelete }) => {
  const navigate = useNavigate();

  return (
    <Card className="h-100 shadow-sm">
      <Card.Img
        variant="top"
        src={blog.cover}
        alt={blog.title}
        style={{
          maxHeight: '200px',
          objectFit: 'contain', // Cambiato da "cover" a "contain"
          backgroundColor: '#f8f9fa', // Aggiunto uno sfondo chiaro per migliorare l'aspetto
        }}
      />
      <Card.Body>
        <div className="d-flex justify-content-between">
          <Badge bg="secondary">{blog.category}</Badge>
          <small className="text-muted">{blog.readTime.value} {blog.readTime.unit}</small>
        </div>
        <Card.Title>{blog.title}</Card.Title>
        <Card.Text>{blog.content.substring(0, 100)}...</Card.Text>
        <Card.Text>
          <small className="text-muted">
            Autore: {blog.author?.firstName} {blog.author?.lastName}
          </small>
        </Card.Text>
        {user && user._id === blog.author?._id && (
          <div className="d-flex gap-2">
            <Button variant="primary" onClick={() => navigate(`/blogs/edit/${blog._id}`)}>
              Modifica
            </Button>
            <Button variant="danger" onClick={() => onDelete(blog._id)}>
              Elimina
            </Button>
          </div>
        )}
      </Card.Body>
    </Card>
  );
};

export default BlogCard;
