import { useState, useEffect } from 'react';
import { Container, Row, Col, Button, Card, Alert } from 'react-bootstrap';
import { useAuth } from '../contexts/AuthContext';
import axios from 'axios';

const Home = () => {
  const { user } = useAuth();
  const [blogs, setBlogs] = useState([]);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchBlogs = async () => {
      try {
        setLoading(true);
        const response = await axios.get('http://localhost:3001/blogs');
        setBlogs(response.data);
      } catch (err) {
        setError('Errore nel caricamento dei blog');
      } finally {
        setLoading(false);
      }
    };

    fetchBlogs();
  }, []);

  const handleDelete = async (id) => {
    if (window.confirm('Sei sicuro di voler eliminare questo blog?')) {
      try {
        await axios.delete(`http://localhost:3001/blogs/${id}`);
        setBlogs((prevBlogs) => prevBlogs.filter((blog) => blog._id !== id));
      } catch (err) {
        setError('Errore durante l\'eliminazione del blog');
      }
    }
  };

  return (
    <Container className="mt-5">
      <h1>Benvenuto nel Blog!</h1>
      {error && <Alert variant="danger">{error}</Alert>}
      <Row>
        {loading ? (
          <p>Caricamento in corso...</p>
        ) : blogs.length > 0 ? (
          blogs.map((blog) => (
            <Col key={blog._id} md={4} className="mb-4">
              <Card>
                <Card.Img variant="top" src={blog.cover} alt={blog.title} />
                <Card.Body>
                  <Card.Title>{blog.title}</Card.Title>
                  <Card.Text>
                    <strong>Categoria:</strong> {blog.category}
                  </Card.Text>
                  <Card.Text>
                    <strong>Tempo di lettura:</strong> {blog.readTime.value} {blog.readTime.unit}
                  </Card.Text>
                  <Card.Text>{blog.content.substring(0, 100)}...</Card.Text>
                  <Card.Text>
                    <small className="text-muted">
                      Autore: {blog.author?.firstName} {blog.author?.lastName}
                    </small>
                  </Card.Text>
                  {user && user._id === blog.author?._id && (
                    <div className="d-flex gap-2">
                      <Button
                        variant="primary"
                        onClick={() => window.location.href = `/blogs/edit/${blog._id}`}
                      >
                        Modifica
                      </Button>
                      <Button
                        variant="danger"
                        onClick={() => handleDelete(blog._id)}
                      >
                        Elimina
                      </Button>
                    </div>
                  )}
                </Card.Body>
              </Card>
            </Col>
          ))
        ) : (
          <p>Nessun blog disponibile</p>
        )}
      </Row>
    </Container>
  );
};

export default Home;
