import axios from '../utils/axios';

export const getAllBlogs = async () => {
    try {
        const response = await axios.get('/blogs');
        return response.data;
    } catch (error) {
        throw new Error(error.response?.data?.message || 'Errore nel recupero dei blog');
    }
};

export const getBlogById = async (id) => {
    try {
        const response = await axios.get(`/blogs/${id}`);
        return response.data;
    } catch (error) {
        throw new Error(error.response?.data?.message || 'Errore nel recupero del blog');
    }
};

export const createBlog = async (blogData) => {
    try {
        const response = await axios.post('/blogs', blogData);
        return response.data;
    } catch (error) {
        throw new Error(error.response?.data?.message || 'Errore nella creazione del blog');
    }
};

export const updateBlog = async (id, blogData) => {
    try {
        const response = await axios.put(`/blogs/${id}`, blogData);
        return response.data;
    } catch (error) {
        throw new Error(error.response?.data?.message || 'Errore nell\'aggiornamento del blog');
    }
};

export const deleteBlog = async (id) => {
    try {
        const response = await axios.delete(`/blogs/${id}`);
        return response.data;
    } catch (error) {
        throw new Error(error.response?.data?.message || 'Errore nell\'eliminazione del blog');
    }
};
