import { Container } from 'react-bootstrap';
import { useAuth } from '../contexts/AuthContext';

const Home = () => {
  const { user } = useAuth();

  return (
    <Container className="mt-5">
      <h1>Benvenuto nel Blog!</h1>
      {user ? (
        <div>
          <h3>Ciao, {user.firstName} {user.lastName}!</h3>
          <p>Email: {user.email}</p>
        </div>
      ) : (
        <p>Effettua il login o registrati per iniziare.</p>
      )}
    </Container>
  );
};

export default Home;
