import React, { useContext, useState, useEffect } from 'react';
import { Container, Card, Button, Row, Col, Spinner, Alert } from 'react-bootstrap';
import { useParams, useNavigate, Link } from 'react-router-dom';
import { BlogContext } from '../contexts/BlogContext';

const BlogDetails = () => {
  const { id } = useParams();
  const { getBlog, deleteBlog } = useContext(BlogContext);
  const navigate = useNavigate();
  const [blog, setBlog] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchBlog = async () => {
      try {
        const data = await getBlog(id);
        setBlog(data);
      } catch (err) {
        setError('Error fetching blog details.');
      } finally {
        setLoading(false);
      }
    };

    fetchBlog();
  }, [id, getBlog]);

  const handleDelete = async () => {
    if (window.confirm('Are you sure you want to delete this blog post?')) {
      try {
        await deleteBlog(id);
        navigate('/');
      } catch (error) {
        console.error('Error deleting blog:', error);
      }
    }
  };

  if (loading) {
    return (
      <Container className="text-center mt-5">
        <Spinner animation="border" role="status">
          <span className="visually-hidden">Loading...</span>
        </Spinner>
      </Container>
    );
  }

  if (error || !blog) {
    return (
      <Container className="mt-4">
        <Alert variant="danger">
          {error || 'Blog not found.'}
        </Alert>
      </Container>
    );
  }

  return (
    <Container className="mt-4">
      <Card>
        <Card.Img variant="top" src={blog.cover} alt={blog.title} />
        <Card.Body>
          <Card.Title as="h1">{blog.title}</Card.Title>
          <Card.Subtitle className="mb-3 text-muted">
            Category: {blog.category}
          </Card.Subtitle>
          <Card.Text>{blog.content}</Card.Text>
          <Card.Text>
            Read Time: {blog.readTime.value} {blog.readTime.unit}
          </Card.Text>
          <Card.Text>
            <small className="text-muted">
              Author: {blog.author.name} {blog.author.lastName}
            </small>
          </Card.Text>
        </Card.Body>
        <Card.Footer className="bg-white">
          <Row>
            <Col>
              <Button as={Link} to="/" variant="secondary" className="me-2">
                Back to Home
              </Button>
              <Button 
                as={Link} 
                to={`/blogs/${id}/edit`} 
                variant="primary"
                className="me-2"
              >
                Edit
              </Button>
              <Button variant="danger" onClick={handleDelete}>
                Delete
              </Button>
            </Col>
          </Row>
        </Card.Footer>
      </Card>
    </Container>
  );
};

export default BlogDetails;
