import { useState, useEffect } from 'react';
import { Form, Button, Container, Alert } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const CreateBlog = () => {
  const navigate = useNavigate();
  const [authors, setAuthors] = useState([]);
  const [error, setError] = useState('');
  const [formData, setFormData] = useState({
    title: '',
    content: '',
    category: '',
    readTime: {
      value: '',
      unit: 'minuti'
    },
    author: '',
    cover: null
  });

  useEffect(() => {
    const fetchAuthors = async () => {
      try {
        const response = await axios.get('http://localhost:3001/authors');
        setAuthors(response.data);
      } catch (err) {
        setError('Errore nel caricamento degli autori');
      }
    };
    fetchAuthors();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    try {
      // Validazione dei dati
      if (!formData.title || !formData.content || !formData.category || !formData.author || !formData.cover) {
        setError('Tutti i campi sono obbligatori');
        return;
      }

      const formDataToSend = new FormData();
      
      // Aggiungiamo i dati al FormData
      formDataToSend.append('title', formData.title);
      formDataToSend.append('content', formData.content);
      formDataToSend.append('category', formData.category);
      formDataToSend.append('author', formData.author);
      formDataToSend.append('cover', formData.cover);
      
      // Gestiamo readTime come oggetto JSON
      const readTimeData = {
        value: parseInt(formData.readTime.value),
        unit: formData.readTime.unit
      };
      formDataToSend.append('readTime', JSON.stringify(readTimeData));

      // Log per debug
      console.log('Dati inviati:', {
        title: formData.title,
        content: formData.content,
        category: formData.category,
        author: formData.author,
        readTime: readTimeData,
        cover: formData.cover
      });

      const response = await axios.post('http://localhost:3001/blogs', formDataToSend, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });

      if (response.data) {
        navigate('/blogs');
      }
    } catch (err) {
      console.error('Errore dettagliato:', err.response?.data);
      setError(err.response?.data?.message || 'Errore durante la creazione del blog');
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    if (name === 'cover') {
      setFormData(prev => ({ ...prev, cover: e.target.files[0] }));
    } else if (name.startsWith('readTime.')) {
      const field = name.split('.')[1];
      setFormData(prev => ({
        ...prev,
        readTime: { ...prev.readTime, [field]: value }
      }));
    } else {
      setFormData(prev => ({ ...prev, [name]: value }));
    }
  };

  return (
    <Container className="mt-4">
      <h2>Crea Nuovo Blog</h2>
      {error && <Alert variant="danger">{error}</Alert>}
      
      <Form onSubmit={handleSubmit}>
        <Form.Group className="mb-3">
          <Form.Label>Titolo</Form.Label>
          <Form.Control
            type="text"
            name="title"
            value={formData.title}
            onChange={handleChange}
            required
          />
        </Form.Group>

        <Form.Group className="mb-3">
          <Form.Label>Categoria</Form.Label>
          <Form.Control
            type="text"
            name="category"
            value={formData.category}
            onChange={handleChange}
            required
          />
        </Form.Group>

        <Form.Group className="mb-3">
          <Form.Label>Contenuto</Form.Label>
          <Form.Control
            as="textarea"
            rows={5}
            name="content"
            value={formData.content}
            onChange={handleChange}
            required
          />
        </Form.Group>

        <Form.Group className="mb-3">
          <Form.Label>Tempo di Lettura</Form.Label>
          <div className="d-flex gap-2">
            <Form.Control
              type="number"
              name="readTime.value"
              value={formData.readTime.value}
              onChange={handleChange}
              required
              min="1"
            />
            <Form.Select
              name="readTime.unit"
              value={formData.readTime.unit}
              onChange={handleChange}
            >
              <option value="minuti">Minuti</option>
              <option value="ore">Ore</option>
            </Form.Select>
          </div>
        </Form.Group>

        <Form.Group className="mb-3">
          <Form.Label>Autore</Form.Label>
          <Form.Select
            name="author"
            value={formData.author}
            onChange={handleChange}
            required
          >
            <option value="">Seleziona un autore</option>
            {authors.map(author => (
              <option key={author._id} value={author._id}>
                {author.name} {author.lastName}
              </option>
            ))}
          </Form.Select>
        </Form.Group>

        <Form.Group className="mb-3">
          <Form.Label>Immagine di Copertina</Form.Label>
          <Form.Control
            type="file"
            name="cover"
            onChange={handleChange}
            accept="image/*"
            required
          />
        </Form.Group>

        <Button variant="primary" type="submit">
          Crea Blog
        </Button>
      </Form>
    </Container>
  );
};

export default CreateBlog;
