import React, { useState, useEffect } from 'react';
import { Form, Button } from 'react-bootstrap';
import axios from 'axios';

const BlogForm = ({ blog, onSubmit }) => {
  const [authors, setAuthors] = useState([]);
  const [formData, setFormData] = useState({
    category: '',
    title: '',
    cover: '',
    content: '',
    readTime: {
      value: '',
      unit: 'minutes'
    },
    author: ''
  });

  useEffect(() => {
    // Fetch authors for the dropdown
    const fetchAuthors = async () => {
      try {
        const response = await axios.get('http://localhost:3001/authors');
        setAuthors(response.data);

        // If no author is selected and we have authors, select the first one
        if (!formData.author && response.data.length > 0) {
          setFormData(prev => ({
            ...prev,
            author: response.data[0]._id
          }));
        }
      } catch (error) {
        console.error('Error fetching authors:', error);
      }
    };

    fetchAuthors();

    // Pre-fill form if editing
    if (blog) {
      setFormData({
        category: blog.category || '',
        title: blog.title || '',
        cover: blog.cover || '',
        content: blog.content || '',
        readTime: {
          value: blog.readTime?.value || '',
          unit: blog.readTime?.unit || 'minutes'
        },
        author: blog.author?._id || blog.author || ''
      });
    }
  }, [blog]);

  const handleChange = (e) => {
    const { name, value } = e.target;

    if (name === 'readTimeValue') {
      setFormData({
        ...formData,
        readTime: {
          ...formData.readTime,
          value: value
        }
      });
    } else if (name === 'readTimeUnit') {
      setFormData({
        ...formData,
        readTime: {
          ...formData.readTime,
          unit: value
        }
      });
    } else {
      setFormData({
        ...formData,
        [name]: value
      });
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(formData);
  };

  return (
    <Form onSubmit={handleSubmit}>
      <Form.Group className="mb-3">
        <Form.Label>Category</Form.Label>
        <Form.Control
          type="text"
          name="category"
          value={formData.category}
          onChange={handleChange}
          required
        />
      </Form.Group>

      <Form.Group className="mb-3">
        <Form.Label>Title</Form.Label>
        <Form.Control
          type="text"
          name="title"
          value={formData.title}
          onChange={handleChange}
          required
        />
      </Form.Group>

      <Form.Group className="mb-3">
        <Form.Label>Cover Image URL</Form.Label>
        <Form.Control
          type="text"
          name="cover"
          value={formData.cover}
          onChange={handleChange}
          required
        />
      </Form.Group>

      <Form.Group className="mb-3">
        <Form.Label>Content</Form.Label>
        <Form.Control
          as="textarea"
          rows={5}
          name="content"
          value={formData.content}
          onChange={handleChange}
          required
        />
      </Form.Group>

      <Form.Group className="mb-3">
        <Form.Label>Read Time</Form.Label>
        <div className="d-flex">
          <Form.Control
            type="number"
            name="readTimeValue"
            value={formData.readTime.value}
            onChange={handleChange}
            style={{ marginRight: '10px' }}
            required
          />
          <Form.Select
            name="readTimeUnit"
            value={formData.readTime.unit}
            onChange={handleChange}
          >
            <option value="minutes">Minutes</option>
            <option value="hours">Hours</option>
          </Form.Select>
        </div>
      </Form.Group>

      <Form.Group className="mb-3">
        <Form.Label>Author</Form.Label>
        <Form.Select
          name="author"
          value={formData.author}
          onChange={handleChange}
          required
        >
          <option value="">Select an author</option>
          {authors.map(author => (
            <option key={author._id} value={author._id}>
              {author.name} {author.lastName}
            </option>
          ))}
        </Form.Select>
      </Form.Group>

      <Button variant="primary" type="submit">
        {blog ? 'Update' : 'Create'} Blog Post
      </Button>
    </Form>
  );
};

export default BlogForm;
