import { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { Container, Row, Col, Card, Button, Alert } from 'react-bootstrap';
import { useAuth } from '../contexts/AuthContext';

const BlogDetail = () => {
    const { id } = useParams();
    const navigate = useNavigate();
    const { user } = useAuth();
    const [blog, setBlog] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    useEffect(() => {
        fetchBlog();
    }, [id]);

    const fetchBlog = async () => {
        try {
            const response = await fetch(`http://localhost:3001/blogs/${id}`);
            const data = await response.json();

            if (!response.ok) {
                throw new Error(data.message || 'Errore nel caricamento del blog');
            }

            setBlog(data);
        } catch (err) {
            setError(err.message);
        } finally {
            setLoading(false);
        }
    };

    const handleDelete = async () => {
        if (!window.confirm('Sei sicuro di voler eliminare questo blog?')) {
            return;
        }

        try {
            const response = await fetch(`http://localhost:3001/blogs/${id}`, {
                method: 'DELETE'
            });

            if (!response.ok) {
                const data = await response.json();
                throw new Error(data.message || 'Errore durante l\'eliminazione del blog');
            }

            navigate('/');
        } catch (err) {
            setError(err.message);
        }
    };

    if (loading) return <div className="text-center mt-5">Caricamento...</div>;
    if (error) return <div className="text-center mt-5 text-danger">{error}</div>;
    if (!blog) return <div className="text-center mt-5">Blog non trovato</div>;

    const isAuthor = user && (blog.author._id === user.id || user.isAdmin);

    return (
        <Container className="mt-4">
            <Row>
                <Col>
                    <Card>
                        {blog.cover && (
                            <Card.Img 
                                variant="top" 
                                src={blog.cover} 
                                alt={blog.title}
                                style={{ height: '400px', objectFit: 'cover' }}
                            />
                        )}
                        <Card.Body>
                            <Card.Title className="h2 mb-4">{blog.title}</Card.Title>
                            <div className="d-flex justify-content-between align-items-center mb-4">
                                <div>
                                    <small className="text-muted">
                                        Autore: {blog.author.name} {blog.author.lastName}
                                    </small>
                                </div>
                                {isAuthor && (
                                    <div>
                                        <Button 
                                            variant="outline-primary" 
                                            className="me-2"
                                            onClick={() => navigate(`/edit-blog/${id}`)}
                                        >
                                            Modifica
                                        </Button>
                                        <Button 
                                            variant="outline-danger"
                                            onClick={handleDelete}
                                        >
                                            Elimina
                                        </Button>
                                    </div>
                                )}
                            </div>
                            <Card.Text className="blog-content">
                                {blog.content}
                            </Card.Text>
                        </Card.Body>
                    </Card>
                </Col>
            </Row>
        </Container>
    );
};

export default BlogDetail;
