import { useState, useEffect } from 'react';
import { Container, Row, Col, Card, Form, Button, Alert } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';

const Home = () => {
    const { user } = useAuth();
    const [blogs, setBlogs] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [searchTerm, setSearchTerm] = useState('');
    const [selectedCategory, setSelectedCategory] = useState('');

    useEffect(() => {
        fetchBlogs();
    }, []);

    const fetchBlogs = async () => {
        try {
            const response = await fetch('http://localhost:3001/blogs');
            const data = await response.json();

            if (!response.ok) {
                throw new Error(data.message || 'Errore nel caricamento dei blog');
            }

            setBlogs(data);
        } catch (err) {
            setError(err.message);
        } finally {
            setLoading(false);
        }
    };

    const filteredBlogs = blogs.filter(blog => {
        const matchesSearch = blog.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
                            blog.content.toLowerCase().includes(searchTerm.toLowerCase());
        const matchesCategory = !selectedCategory || blog.category === selectedCategory;
        return matchesSearch && matchesCategory;
    });

    const categories = [...new Set(blogs.map(blog => blog.category))];

    if (loading) {
        return (
            <Container className="mt-5">
                <div className="text-center">Caricamento...</div>
            </Container>
        );
    }

    if (error) {
        return (
            <Container className="mt-5">
                <Alert variant="danger">{error}</Alert>
            </Container>
        );
    }

    return (
        <Container className="mt-5">
            <Row className="mb-4">
                <Col md={8}>
                    <Form.Control
                        type="text"
                        placeholder="Cerca nei blog..."
                        value={searchTerm}
                        onChange={(e) => setSearchTerm(e.target.value)}
                    />
                </Col>
                <Col md={4}>
                    <Form.Select
                        value={selectedCategory}
                        onChange={(e) => setSelectedCategory(e.target.value)}
                    >
                        <option value="">Tutte le categorie</option>
                        {categories.map(category => (
                            <option key={category} value={category}>
                                {category.charAt(0).toUpperCase() + category.slice(1)}
                            </option>
                        ))}
                    </Form.Select>
                </Col>
            </Row>

            {user && (
                <Row className="mb-4">
                    <Col>
                        <Button 
                            variant="primary" 
                            as={Link} 
                            to="/create-blog"
                        >
                            Crea nuovo blog
                        </Button>
                    </Col>
                </Row>
            )}

            <Row>
                {filteredBlogs.map(blog => (
                    <Col key={blog._id} md={6} lg={4} className="mb-4">
                        <Card>
                            {blog.cover && (
                                <Card.Img 
                                    variant="top" 
                                    src={blog.cover} 
                                    alt={blog.title}
                                    style={{ height: '200px', objectFit: 'cover' }}
                                />
                            )}
                            <Card.Body>
                                <Card.Title>{blog.title}</Card.Title>
                                <Card.Text>
                                    {blog.content.substring(0, 150)}...
                                </Card.Text>
                                <div className="d-flex justify-content-between align-items-center">
                                    <div>
                                        <small className="text-muted">
                                            {blog.author.firstName} {blog.author.lastName}
                                        </small>
                                        <br />
                                        <small className="text-muted">
                                            {blog.readTime.value} {blog.readTime.unit} di lettura
                                        </small>
                                    </div>
                                    <Button 
                                        variant="outline-primary" 
                                        as={Link} 
                                        to={`/blog/${blog._id}`}
                                    >
                                        Leggi
                                    </Button>
                                </div>
                            </Card.Body>
                        </Card>
                    </Col>
                ))}
            </Row>

            {filteredBlogs.length === 0 && (
                <Alert variant="info">
                    Nessun blog trovato per i criteri di ricerca selezionati.
                </Alert>
            )}
        </Container>
    );
};

export default Home;
