import { Card, Button } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';

const PostCard = ({ post }) => {
  const { user } = useAuth();

  return (
    <Card className="mb-4">
      <Card.Header className="d-flex justify-content-between align-items-center">
        <div>
          <strong>{post.author.name} {post.author.lastName}</strong>
          <span className="text-muted ms-2">• {new Date(post.createdAt).toLocaleDateString()}</span>
        </div>
        <span className="badge bg-primary">{post.category}</span>
      </Card.Header>
      <Card.Body>
        <Card.Title>{post.title}</Card.Title>
        <Card.Text>
          {post.content.length > 200 
            ? `${post.content.substring(0, 200)}...` 
            : post.content}
        </Card.Text>
        <div className="d-flex justify-content-between align-items-center">
          <Link to={`/blogs/${post._id}`}>
            <Button variant="outline-primary">Leggi di più</Button>
          </Link>
          {user && (user._id === post.author._id || user.isAdmin) && (
            <div>
              <Link to={`/blogs/${post._id}/edit`}>
                <Button variant="outline-secondary" className="me-2">Modifica</Button>
              </Link>
              <Button variant="outline-danger">Elimina</Button>
            </div>
          )}
        </div>
      </Card.Body>
    </Card>
  );
};

export default PostCard;
