import { useState, useEffect } from 'react';
import { Form, Button, Container, Alert } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const CreateBlog = () => {
  const [formData, setFormData] = useState({
    title: '',
    content: '',
    category: '',
    readTime: {
      value: 0,
      unit: 'minuti'
    },
    author: '',
    cover: null
  });
  const [authors, setAuthors] = useState([]);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    fetchAuthors();
  }, []);

  const fetchAuthors = async () => {
    try {
      const response = await axios.get('http://localhost:3001/authors');
      setAuthors(response.data);
    } catch (err) {
      console.error('Errore nel caricamento degli autori:', err);
      setError('Errore nel caricamento degli autori');
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    // Validazione dei dati prima dell'invio
    if (!formData.title || !formData.content || !formData.category || 
        !formData.author || !formData.cover || !formData.readTime.value || 
        !formData.readTime.unit) {
      setError('Tutti i campi sono obbligatori');
      return;
    }

    try {
      const formDataToSend = new FormData();
      
      // Aggiungiamo tutti i campi richiesti
      formDataToSend.append('title', formData.title.trim());
      formDataToSend.append('content', formData.content.trim());
      formDataToSend.append('category', formData.category.trim());
      formDataToSend.append('author', formData.author);
      
      // Gestiamo readTime come oggetto
      formDataToSend.append('readTime[value]', parseInt(formData.readTime.value));
      formDataToSend.append('readTime[unit]', formData.readTime.unit);
      
      // Gestiamo il file cover
      if (formData.cover) {
        formDataToSend.append('cover', formData.cover);
      }

      // Log dei dati per debug
      console.log('Dati da inviare:', {
        title: formData.title,
        content: formData.content,
        category: formData.category,
        readTime: formData.readTime,
        author: formData.author,
        cover: formData.cover
      });

      // Log del FormData
      for (let pair of formDataToSend.entries()) {
        console.log(pair[0] + ': ' + pair[1]);
      }

      const response = await axios.post('http://localhost:3001/blogs', formDataToSend, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });

      console.log('Risposta dal server:', response.data);
      navigate('/blogs');
    } catch (err) {
      console.error('Errore dettagliato:', err.response?.data || err);
      setError(err.response?.data?.message || 'Errore durante la creazione del blog');
    }
  };

  const handleChange = (e) => {
    if (e.target.name === 'cover') {
      setFormData({ ...formData, cover: e.target.files[0] });
    } else if (e.target.name.startsWith('readTime.')) {
      const field = e.target.name.split('.')[1];
      setFormData({
        ...formData,
        readTime: { 
          ...formData.readTime, 
          [field]: field === 'value' ? parseInt(e.target.value) : e.target.value 
        }
      });
    } else {
      setFormData({ ...formData, [e.target.name]: e.target.value });
    }
  };

  return (
    <Container className="mt-4">
      <h2>Crea Nuovo Blog</h2>
      {error && <Alert variant="danger">{error}</Alert>}
      <Form onSubmit={handleSubmit}>
        <Form.Group className="mb-3">
          <Form.Label>Titolo</Form.Label>
          <Form.Control
            type="text"
            name="title"
            value={formData.title}
            onChange={handleChange}
            required
          />
        </Form.Group>
        <Form.Group className="mb-3">
          <Form.Label>Contenuto</Form.Label>
          <Form.Control
            as="textarea"
            rows={10}
            name="content"
            value={formData.content}
            onChange={handleChange}
            required
          />
        </Form.Group>
        <Form.Group className="mb-3">
          <Form.Label>Categoria</Form.Label>
          <Form.Control
            type="text"
            name="category"
            value={formData.category}
            onChange={handleChange}
            required
          />
        </Form.Group>
        <Form.Group className="mb-3">
          <Form.Label>Tempo di Lettura</Form.Label>
          <div className="d-flex gap-2">
            <Form.Control
              type="number"
              name="readTime.value"
              value={formData.readTime.value}
              onChange={handleChange}
              required
              min="1"
            />
            <Form.Select
              name="readTime.unit"
              value={formData.readTime.unit}
              onChange={handleChange}
              required
            >
              <option value="minuti">Minuti</option>
              <option value="ore">Ore</option>
            </Form.Select>
          </div>
        </Form.Group>
        <Form.Group className="mb-3">
          <Form.Label>Autore</Form.Label>
          <Form.Select
            name="author"
            value={formData.author}
            onChange={handleChange}
            required
          >
            <option value="">Seleziona un autore</option>
            {authors.map(author => (
              <option key={author._id} value={author._id}>
                {author.name} {author.lastName}
              </option>
            ))}
          </Form.Select>
        </Form.Group>
        <Form.Group className="mb-3">
          <Form.Label>Immagine di copertina</Form.Label>
          <Form.Control
            type="file"
            name="cover"
            onChange={handleChange}
            accept="image/*"
            required
          />
        </Form.Group>
        <Button variant="primary" type="submit">
          Crea Blog
        </Button>
      </Form>
    </Container>
  );
};

export default CreateBlog;
