import { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { Form, Button, Container, Row, Col, Alert } from 'react-bootstrap';
import { useAuth } from '../contexts/AuthContext';

const EditBlog = () => {
    const { id } = useParams();
    const navigate = useNavigate();
    const { user } = useAuth();
    const [formData, setFormData] = useState({
        title: '',
        content: '',
        category: '',
        readTime: {
            value: '',
            unit: 'minuti'
        },
        cover: null
    });
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(true);
    const [saving, setSaving] = useState(false);

    useEffect(() => {
        const fetchBlog = async () => {
            try {
                const response = await fetch(`http://localhost:3001/blogs/${id}`);
                const data = await response.json();

                if (!response.ok) {
                    throw new Error(data.message || 'Errore nel caricamento del blog');
                }

                setFormData({
                    title: data.title,
                    content: data.content,
                    category: data.category,
                    readTime: data.readTime,
                    cover: null
                });
            } catch (err) {
                setError(err.message);
            } finally {
                setLoading(false);
            }
        };

        fetchBlog();
    }, [id]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        if (name === 'readTime.value') {
            setFormData(prev => ({
                ...prev,
                readTime: {
                    ...prev.readTime,
                    value
                }
            }));
        } else if (name === 'readTime.unit') {
            setFormData(prev => ({
                ...prev,
                readTime: {
                    ...prev.readTime,
                    unit: value
                }
            }));
        } else {
            setFormData(prev => ({
                ...prev,
                [name]: value
            }));
        }
    };

    const handleFileChange = (e) => {
        setFormData(prev => ({
            ...prev,
            cover: e.target.files[0]
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError('');
        setSaving(true);

        try {
            const formDataToSend = new FormData();
            formDataToSend.append('title', formData.title);
            formDataToSend.append('content', formData.content);
            formDataToSend.append('category', formData.category);
            formDataToSend.append('readTime[value]', formData.readTime.value);
            formDataToSend.append('readTime[unit]', formData.readTime.unit);

            if (formData.cover) {
                formDataToSend.append('cover', formData.cover);
            }

            const response = await fetch(`http://localhost:3001/blogs/${id}`, {
                method: 'PUT',
                body: formDataToSend
            });

            const data = await response.json();

            if (!response.ok) {
                throw new Error(data.message || 'Errore durante l\'aggiornamento del blog');
            }

            navigate(`/blog/${id}`);
        } catch (err) {
            setError(err.message || 'Errore durante l\'aggiornamento del blog');
        } finally {
            setSaving(false);
        }
    };

    if (loading) {
        return (
            <Container className="mt-5">
                <div className="text-center">Caricamento...</div>
            </Container>
        );
    }

    return (
        <Container className="mt-5">
            <Row className="justify-content-md-center">
                <Col md={8}>
                    <div className="card">
                        <div className="card-body">
                            <h2 className="card-title text-center mb-4">Modifica Blog</h2>
                            {error && <Alert variant="danger">{error}</Alert>}
                            <Form onSubmit={handleSubmit}>
                                <Form.Group className="mb-3">
                                    <Form.Label>Titolo</Form.Label>
                                    <Form.Control
                                        type="text"
                                        name="title"
                                        value={formData.title}
                                        onChange={handleChange}
                                        required
                                    />
                                </Form.Group>
                                <Form.Group className="mb-3">
                                    <Form.Label>Categoria</Form.Label>
                                    <Form.Select
                                        name="category"
                                        value={formData.category}
                                        onChange={handleChange}
                                        required
                                    >
                                        <option value="">Seleziona una categoria</option>
                                        <option value="tecnologia">Tecnologia</option>
                                        <option value="cultura">Cultura</option>
                                        <option value="sport">Sport</option>
                                        <option value="politica">Politica</option>
                                    </Form.Select>
                                </Form.Group>
                                <Form.Group className="mb-3">
                                    <Form.Label>Tempo di lettura</Form.Label>
                                    <div className="d-flex gap-2">
                                        <Form.Control
                                            type="number"
                                            name="readTime.value"
                                            value={formData.readTime.value}
                                            onChange={handleChange}
                                            required
                                            min="1"
                                        />
                                        <Form.Select
                                            name="readTime.unit"
                                            value={formData.readTime.unit}
                                            onChange={handleChange}
                                            required
                                        >
                                            <option value="minuti">Minuti</option>
                                            <option value="ore">Ore</option>
                                        </Form.Select>
                                    </div>
                                </Form.Group>
                                <Form.Group className="mb-3">
                                    <Form.Label>Contenuto</Form.Label>
                                    <Form.Control
                                        as="textarea"
                                        rows={10}
                                        name="content"
                                        value={formData.content}
                                        onChange={handleChange}
                                        required
                                    />
                                </Form.Group>
                                <Form.Group className="mb-3">
                                    <Form.Label>Immagine di copertina</Form.Label>
                                    <Form.Control
                                        type="file"
                                        name="cover"
                                        onChange={handleFileChange}
                                        accept="image/*"
                                    />
                                    <Form.Text className="text-muted">
                                        Lascia vuoto per mantenere l'immagine attuale
                                    </Form.Text>
                                </Form.Group>
                                <Button 
                                    variant="primary" 
                                    type="submit" 
                                    className="w-100"
                                    disabled={saving}
                                >
                                    {saving ? 'Salvataggio in corso...' : 'Salva Modifiche'}
                                </Button>
                            </Form>
                        </div>
                    </div>
                </Col>
            </Row>
        </Container>
    );
};

export default EditBlog;
