import { useState, useEffect } from 'react';
import { Container, Row, Col, Spinner, Alert } from 'react-bootstrap';
import { useAuth } from '../contexts/AuthContext';
import PostCard from '../components/PostCard';
import axios from 'axios';

const MyPosts = () => {
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const { user } = useAuth();

  useEffect(() => {
    fetchMyPosts();
  }, []);

  const fetchMyPosts = async () => {
    try {
      const response = await axios.get(`/api/blogs/author/${user._id}`);
      setPosts(response.data);
      setLoading(false);
    } catch (err) {
      setError('Errore nel caricamento dei tuoi post');
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <Container className="text-center mt-5">
        <Spinner animation="border" role="status">
          <span className="visually-hidden">Caricamento...</span>
        </Spinner>
      </Container>
    );
  }

  if (error) {
    return (
      <Container className="mt-5">
        <Alert variant="danger">{error}</Alert>
      </Container>
    );
  }

  return (
    <Container className="mt-5">
      <h2 className="mb-4">I Miei Post</h2>
      {posts.length === 0 ? (
        <Alert variant="info">
          Non hai ancora creato nessun post. Crea il tuo primo post!
        </Alert>
      ) : (
        <Row>
          {posts.map(post => (
            <Col key={post._id} md={6} lg={4}>
              <PostCard post={post} />
            </Col>
          ))}
        </Row>
      )}
    </Container>
  );
};

export default MyPosts;
